import React, {FC} from 'react'
import { DealDetail } from './DealDetail.tsx';

interface Props {
    id: number;
    title: string;
    imageUrl: string;
    price: string;
    tiket: string;
    yieldPercent: string;
    daysLeft: string;
    sold: string;
}

export const DealCard: FC<Props> = ({id, title, imageUrl, price, tiket, yieldPercent, daysLeft, sold}) => {
return (
    <div className='shrink-0 w-[49.5%] 2xl:w-[630px] h-[400px] bg-no-repeat bg-cover bg-center rounded-[5px] flex items-end p-[14px]' style={{backgroundImage: `url(${imageUrl})`}}>
        <div>
            <div>
                <h3 className='text-left font-bold text-[20px] leading-[34px]'>{title}</h3>
            </div>
            <div className='grid grid-cols-3 grid-rows-2 gap-x-[100px]'>
                <DealDetail>{`${price} Dhs`}</DealDetail>
                <DealDetail>{`Yield ${yieldPercent}%`}</DealDetail>
                <DealDetail>{`Sold ${sold}%`}</DealDetail>
                <DealDetail>{`Tiket - ${tiket} Dhs`}</DealDetail>
                <DealDetail>{`Days left ${daysLeft}`}</DealDetail>
            </div>
        </div>
    </div>
)
}
