import React, {FC, useId} from 'react'

interface Props {
    inputType: 'email' | 'password';
    labelText: string;
}

export const LoginInput: FC<Props> = ({inputType, labelText}) => {
    const id = useId()
return (
    <div className="flex flex-col ">
        <label htmlFor = {id} className='font-bold leading-[20px] text-[14px]'>{labelText}</label>
        <input className='w-full rounded-[5px] py-[14px] px-[20px] h-[50px] font-sans font-normal leading-[22px] text-[14px] text-dark-blue bg-[#E0E0E0]' id={id} type={inputType} placeholder={`${inputType.substring(0,1).toUpperCase()}${inputType.substring(1,inputType.length)}`}/>
    </div>
)
}
